INSERT INTO `questions` VALUES (1,1,'निम्नलिखित में से किस विकल्प में अलंकार व उससे संबंधित उदाहरण सुमेलित नहीं है?','रूपक - \'नील परिधान बीच सुकुमार खुल रहा मृदुल-अधखुला अंग। खिला हो ज्यों बिजली का फूल, मेघवन बीच गुलाबी रंग।\'','उपमा - \'पीपर पात सरिस मन डोला।\'','श्लेष - \'सुबरन को ढूँढ़त फिरत, कवि, व्यभिचारी, चोर।\'','मानवीकरण - \'संध्या घनमाला की सुंदर ओढ़े रंग-बिरंगी छीट।\'','a','Ans. (a): \'नील परिधान बीच सुकुमार खुल रहा मृदुल-अधखुला अंग। खिला हो ज्यों बिजली का फूल, मेघवन बीच गुलाबी रंग।\' उक्त पंक्ति रूपक अलंकार का उदाहरण नहीं है बल्कि यह उत्प्रेक्षा अलंकार का उदाहरण है। उत्प्रेक्षा - जहाँ उपमेय में उपमान की सम्भावना की जाती है, वहाँ उत्प्रेक्षा अलंकार होता है। रूपक - समानता के कारण उपमेय में उपमान का आरोप कर भेद स्थापित किया जाए वहाँ रूपक अलंकार होता है। जैसे - उदित उदय गिरि मंच पर, रघुवर बाल पतंग। अन्य सभी अलंकार व उससे संबंधित उदाहरण का सुमेलन सही है।','Haryana (CET) -06/11/2022 Shift-I',1,'2026-01-15 12:56:19'),(2,1,'कैधौं रितुराज काज, अवनि उसाँस लेता किधौं यह ग्रीष्म की, भीषण लुआर है।\' किस अलंकार का उदाहरण है?','संदेह','भ्रांतिमान','श्लेष','रूपक','a','Ans. (a): कैधौं रितुराज काज, अवनि उसाँस लेता किधौं यह ग्रीष्म की, भीषण लुआर है। उक्त पंक्ति संदेह अलंकार का उदाहरण है। इस उदाहरण में बताया गया है कि ग्रीष्म ऋतु की भयंकर लू की लपटें हैं या बसन्त के विरह में पृथ्वी के अन्तस से निकलती हुई विरह दुःख की आँख इसमें संदेह उत्पन्न होता है। संदेह अलंकार - जहाँ किसी वस्तु को देखकर संशय बना रहे, निश्चय न हो उसे संदेह अलंकार कहते हैं।','Haryana (CET) Exam-05/11/2022 Shift-II',2,'2026-01-15 12:56:19'),(3,1,'बीती विभावरी जाग री। अंबर पनघट में डुबो रही तारा-घट उषा नागरी।\' उपर्युक्त काव्य-पंक्तियाँ उदाहरण है:','अन्योक्ति अलंकार का','व्यतिरेक अलंकार का','यमक अलंकार का','मानवीकरण अलंकार का','d','Ans. (d): \'बीती विभावरी जाग री। अंबर पनघट में डुबो रही तारा-घट उषा नागरी।\'','Haryana (CET) -06/11/2022 Shift-I',3,'2026-01-15 12:56:19'),(4,1,'मानवीकरण- जब प्राकृतिक वस्तुओं जैसे पेड़, पौधे, बादल आदि मानवी भावनाओं का वर्णन हो यानि निर्जीव चीजों में सजीव होना पाया जाए तब वहाँ मानवीकरण अलंकार होता है। \'चारु चंद्र की चंचल किरणें\' ___________ अलंकार का उदाहरण है।','अनुप्रास','श्लेष','रूपक','इनमें से कोई नहीं','a','Ans. (a): कोई अक्षर जब किसी कविता में बार-बार प्रकट हो तो वहाँ अनुप्रास अलंकार होता है। अतः \'चारु चंद्र की चंचल किरणें\' अनुप्रास अलंकार का उदाहरण है।','Haryana Police Cons. 01/11/2021 Shift-III',4,'2026-01-15 12:56:19'),(5,1,'काव्य का सौंदर्य बढ़ाने वाले तत्व को ___________ कहते हैं।','चमत्कार','कविता','अलंकार','इनमें से कोई नहीं','c','Ans. (c): काव्य का सौंदर्य (शोभा) बढ़ाने वाले तत्व या शब्द को अलंकार कहते हैं। अलंकार के मुख्यतः दो भेद होते हैं (1) शब्दालंकार (2) अर्थालंकार','Haryana Police Cons. Ex-02/11/2021 Shift-III(P1)',5,'2026-01-15 12:56:19'),(6,1,'सुनाई किसने पल में आन, कान में मधुमय मोहक तान? तरी को ले जाओ मँझधारा। डूबकर हो जाओगे पार॥ किस अलंकार का उदाहरण है?','उत्प्रेक्षा','यमक','विरोधाभास','रूपक','c','Ans. (c): सुनाई किसने पल में आन, कान में मधुमय मोहक तान? \'तरी को ले जाओ मँझधारा। डूबकर हो जाओगे पार।\'\'\' उक्त पंक्ति \'विरोधाभास अलंकार\' का उदाहरण है। विरोधाभास अलंकार - जहाँ वास्तविक विरोध न होकर केवल विरोध का आभास हो, वहाँ विरोधाभास अलंकार होता है।','HSSC (TGT Hindi)Exam-2015 Shift-I',6,'2026-01-15 12:56:19'),(7,1,'मानवीकरण\' अलंकार का प्रयोग किस प्रकार की कविता में हुआ है?','दोहा में','चौपाई में','आदिकालीन कविता में','नई कविता में','d','Ans. (d): \'मानवीकरण\' अलंकार का प्रयोग नई कविता में हुआ है।','HSSC (TGT Hindi)Exam-2015 Shift-I',7,'2026-01-15 12:56:19'),(8,1,'जहाँ किसी वस्तु का लोक-सीमा से इतना बढ़कर वर्णन किया जाए की वह असम्भव की सीमा तक पहुँच जाए, वहाँ अलंकार होता है-','अतिशयोक्ति','उत्प्रेक्षा','अन्योक्ति','विरोधाभास','a','Ans. (a): जहाँ किसी वस्तु का लोक-सीमा से इतना बढ़कर वर्णन किया जाए की वह असम्भव की सीमा तक पहुँच जाए, वहाँ अतिशयोक्ति अलंकार होता है।','HSSC (TGT Hindi)Exam-2015 Shift-I',8,'2026-01-15 12:56:19'),(9,1,'सोहत ओढ़े पीत पट स्याम सलोने गात। मनहुँ नील मनि सैल पर आतप पर्यो प्रभात।। उक्त पंक्ति में कौन सा अलंकार है?','यमक','उत्प्रेक्षा','रूपक','श्लेष','b','सोहत ओढ़े पीत पट स्याम सलोने गात। मनहुँ नील मणि सैल पर आतप पर्यो प्रभात।।\' उक्त पंक्ति में उत्प्रेक्षा अलंकार है।','HSSC (PGT Sociology) Exam-2015',9,'2026-01-15 12:56:19'),(10,1,'ज्यौं-ज्यौं बूड़े स्याम, त्यों-त्यों उज्ज्वल होय\' उक्त पंक्ति में कौन सा अलंकार है?','उत्प्रेक्षा','विरोधाभास','यमक','उपमा','b','ज्यौं-ज्यौं बूड़े स्याम, त्यों-त्यों उज्जवल होय\' उक्त पंक्ति में विरोधाभास अलंकार है।','HSSC PGT (Commerce) Exam-2015',10,'2026-01-15 12:56:19'),(11,1,'तरनि तनूजा तट तमाल तरुवर बहु छाए\' उक्त पंक्ति में कौन सा अलंकार है?','अनुप्रास','यमक','उत्प्रेक्षा','उपमा','a','तरनि तनूजा तट तमाल तरुवर बहु छाए\' उक्त पंक्ति में अनुप्रास अलंकार है।','HSSC PGT (Com.Sci.) Exam-2015 HSSC (JE) Exam-12/04/2018 Haryana Patawari-2015',11,'2026-01-15 12:56:19'),(12,1,'माला फेरत जुग गया, फिरा न मन का फेर। कर का मनका डारि दे, मन का मनका फेर।।\' उक्त पंक्ति में कौन सा अलंकार है?','यमक','अनुप्रास','श्लेष','रूपक','a','माला फेरत जुग गया, फिरा न मन का फेर। कर का मनका डारि दे, मन का मनका फेर।।\' उक्त पंक्ति में \'यमक\' अलंकार है। यमक अलंकार - जहाँ एक या एक से अधिक शब्द एक से अधिक बार प्रयुक्त हो एवं अर्थ भी प्रत्येक बार भिन्न हो, वहाँ यमक अलंकार होता है।','HSSC PGT English (Mewat codre) Haryana patawari 01/05/2016 Haryana (Cleark) Exam 27/11/2010 Shift-I',12,'2026-01-15 12:56:19'),(13,1,'सुख-दुख के मधुर मिलन से यह जीवन हो परिपूरण। फिर घन में ओझल हो शशि, फिर शशि में ओझल हो घन।।\' उक्त पंक्तियों में कौन सा अलंकार है?','श्लेष','उपमा','रूपक','दृष्टान्त','d','सुख-दुख के मधुर मिलन से यह जीवन हो परिपूरण। फिर घन में ओझल हो शशि, फिर शशि में ओझल हो घन।।\' उक्त पंक्ति दृष्टान्त अलंकार का है। दृष्टान्त अलंकार - जहाँ दोनों सामान्य या दोनों विशेष वाक्यों में बिम्ब प्रतिबिम्ब भाव होता है, वहाँ पर दृष्टान्त अलंकार होता है।','HSSC (PGT Math) Exam-2015',13,'2026-01-15 12:56:19'),(14,1,'वर्ग \'अ\' में दिए गए अलंकारों के उदाहरणों को उनके नाम के साथ वर्ग \'ब\' मिलान कीजिए: वर्ग \'अ\' A. पीपर पात सरिस मन डोला B. आए महंत बसंत C. चमक गई चपला चम-चम D. देख लो साकेत नगरी है यही स्वर्ग से मिलने गगन में जा रही वर्ग \'ब\' 1. अनुप्रास अलंकार 2. उपमा अलंकार 3. अतिशयोक